.wkit-wa-inner-wrap{
    width: 350px;
    position: relative;
    display: inline-block;
}
.wkit-wa-popup-close {
    position: absolute;
    right: 10px;
    top: 10px;
    font-size: 20px;
    color: #fff;
    opacity: 0.8;
    transition: 0.3s linear;
    cursor: pointer;
    line-height: normal;
    transform: rotate(45deg);
}
.wkit-wa-popup-close:hover{
    opacity: 1;
}
.wkit-whatsapp-chat-inner {
    display: inline-block;
    width: 100%;
    overflow: hidden;
    visibility: hidden;
    opacity: 0;
    transform: translateY(50%);
    position: absolute;
    z-index: 99999;
    bottom: 100%;
    left: 0;
    transition: 0.3s linear;
}
.wkit-whatsapp-chat-inner.active{
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
}
.wkit-whatsapp-header{
    background: rgb(9, 94, 84);
    color: rgb(17, 17, 17);
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    padding: 24px 20px;
    position: relative;
}
.header-avatar{
    position: relative;
    width: max-content;
    height: max-content;
    box-shadow: rgba(17, 17, 17, 0.1) 0px 0px 2px inset;
    border-radius: 50%;
}
.header-avatar::before{
    content: "";
    bottom: 4px;
    right: 2px;
    width: 12px;
    height: 12px;
    box-sizing: border-box;
    position: absolute;
    z-index: 1;
    border-radius: 50%;
    background-color: rgb(74, 213, 4);
    display: block;
    border: 2px solid rgb(9, 94, 84);
}
.avatar-img-wrap {
    width: 52px;
    height: 52px;
    transition: opacity 1s ease-out 0s;
    background-color: rgb(210, 210, 210);
    opacity: 1;
    border-radius: 50%;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
}

img.avatar-image {
    width: 100%;
    height: 100%; 
    object-fit: cover; 
    border-radius: inherit; 
}
.header-content{
    margin-left: 16px;
    margin-right: 16px;
    width: 100%;
    overflow: hidden;
    text-align: left;
}
.header-name{
    width: 100%;
    font-size: 16px;
    font-weight: 700;
    line-height: 20px;
    max-height: 60px;
    color: rgb(255, 255, 255);
}
.header-name, .header-name .tp-title-icon, .header-time, .header-time .tp-title-icon{
    display: inline-flex;
    align-items: center;
}
 .header-name .tp-title-icon{
     font-size: 16px;
     color: #fff;
 }
 .header-name .tp-title-icon .dashicon{
     font-size: 16px;
     width: auto;
    height: auto;
 }
.header-name, .header-time{
    gap: 5px;
}
.header-time{
    font-size: 13px;
    line-height: 18px;
    margin-top: 4px;
    color: rgb(255, 255, 255);
}
.header-time .tp-title-icon{
    font-size: 13px;
     color: #fff;
}
.header-time .tp-title-icon .dashicon{
     font-size: 13px;
     width: auto;
    height: auto;
 }
.wkit-whatsapp-body{
    padding: 20px;
    background-color: rgb(230, 221, 212);
    position: relative;
    overflow: auto;
    max-height: 380px;
}
.whatsapp-typing-loader{
    background-color: rgb(255, 255, 255);
    width: 52.5px;
    height: 32px;
    border-radius: 16px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-left: 10px;
    opacity: 0; 
    z-index: 11111111111111111;
    transition: all 0.1s ease 0s;
    z-index: 11;
    box-shadow: rgba(0, 0, 0, 0.13) 0px 1px 0.5px;
    position: absolute;
    --wdotColor1: rgb(182, 181, 186);
    --wdotColor2: rgb(17, 17, 17);
}
.what-dot{
    height: 5px;
    width: 5px;
    margin: 0px 2px;
    background-color: ;
    border-radius: 50%;
    position: relative;
    animation-duration: 1.2s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
}
.dot-1{
    animation-name: dot_1_anim;
    background-color: var(--wdotColor1);
}
.dot-2{
    animation-name: dot_2_anim;
}
.dot-3{
    animation-name: dot_3_anim;
}
.wkit-toggle-btn.after{
    flex-direction: row-reverse;
}
@keyframes dot_1_anim {
    0% {
        background-color: var(--wdotColor1);
    }
    15%{
        background-color: var(--wdotColor2);
    }
    25%{
        background-color: var(--wdotColor1);
    }
}
@keyframes dot_2_anim {
    15% {
        background-color: var(--wdotColor1);
    }
    25%{
        background-color: var(--wdotColor2);
    }
    35%{
        background-color: var(--wdotColor1);
    }
}
@keyframes dot_3_anim {
    25% {
        background-color: var(--wdotColor1);
    }
    35%{
        background-color: var(--wdotColor2);
    }
    45%{
        background-color: var(--wdotColor1);
    }
}
.body-main-content{
    padding: 7px 14px 6px;
    position: relative;
    transition: all 0.3s ease 0s;
    opacity: 0;
    transform-origin: center top;
    z-index: 2;
    color: rgb(17, 17, 17);
    font-size: 15px;
    line-height: 1.39;
    max-width: calc(100% - 66px);
    border-radius: 0px 8px 8px;
    background-color: rgb(255, 255, 255);
    margin-top: 4px;
    text-align: left;
}
.body-main-content::before{
    position: absolute;
    background-image: url(data:image/png;base64,);
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: contain;
    content: "";
    top: 0px;
    left: -12px;
    width: 12px;
    height: 19px;
}
.author-name{
    font-size: 13px;
    font-weight: 700;
    line-height: 18px;
    color: rgba(0, 0, 0, 0.4);
}
.content-text{
    font-size: 14px;
    line-height: 19px;
    margin-top: 4px;
    color: rgb(17, 17, 17);
}
.content-time{
    text-align: right;
    margin-top: 4px;
    font-size: 12px;
    line-height: 16px;
    color: rgba(17, 17, 17, 0.5);
    margin-right: -8px;
    margin-bottom: -4px;
}
.wkit-wa-btn-wrap{
    display: block;
    padding: 20px;
}
.type-none .tp-title-icon, [data-text=""], [data-src=""]{
    display: none;
}
.type-icon .tp-title-icon svg{
    width: 20px;
    height: auto;
}
.tp-title-icon, .wa-btn-text, .wkit-whatsapp-btn{
    transition: 0.3s linear;
}
.wkit-whatsapp-btn {
    border-radius: 24px;
    width: 100%;
    display: flex;
    gap: 5px;
    color: rgb(255, 255, 255);
    font-weight: bold;
    justify-content: center;
    align-items: center;
    font-size: 16px;
    background-color: rgb(20, 198, 86);
    border: 0;
    padding: 10px;
    text-decoration: none;
}
.wkit-whatsapp-btn .tp-title-icon{
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-size: 16px;
    color: #fff;
    transition: 0.3s linear;
}
.wkit-whatsapp-btn .tp-title-icon .dashicon{
     font-size: 16px;
    width: auto;
    height: auto;
    transition: 0.3s linear;
}
.wkit-toggle-btn{
    width: max-content;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    padding: 10px 20px;
    background: #000;
    color: #fff;
    font-size: 16px;
    gap: 10px;
    border-radius: 30px;
    position: relative;
    cursor: pointer;
    transition: 0.3s linear;
}
.wkit-toggle-btn.after{
    flex-direction: row-reverse;
}
.wkit-toggle-btn .tp-title-icon{
    display: inline-flex;
    align-items: center;
    justify-content: center;
    color: #fff;
    font-size: 16px;
}
.wkit-toggle-btn .tp-title-icon .dashicon{
   font-size: 16px;
   width: auto;
   height: auto;
}
.wkit-toggle-btn.dot-show-yes::before{
    content: '';
    width: 10px;
    height: 10px;
    background-color: rgb(255, 0, 0);
    position: absolute;
    z-index: 1;
    border-radius: 50%;
    right: 4px;
    top: 4px;
    transition: 0.3s linear;
}
@media(max-width:767px) {
    .header-avatar::before{
        right: 3px;
        bottom: 0px; 
    }
}

