.wkit-imghover-wrapper{
    width: fit-content;
}
.wkit-imghover-wrapper .wkit-img-box{
    width: 300px;
    height: 275px;
    overflow:hidden;
    position: relative;
    cursor: pointer;
}
.wkit-imghover-wrapper a{
    text-decoration: none;
}
.wkit-img-box > .wkit-img-cover{
    position: relative;
    width: 100%;
    height: 100%;
    z-index: 9;
}
.wkit-img-box:hover > .wkit-img-cover > .wkit-bg-img{
    opacity: 0;
    transition: opacity 300ms ;
}
.wkit-imghover-wrapper [data-title=""],.wkit-imghover-wrapper [data-label=""] {
    display: none;
}
.wkit-img-cover > .wkit-bg-img{
    position: absolute;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: 50% 50%;
    transition: opacity 300ms ease-in-out;
    overflow:hidden;
}
.wkit-img-cover > .wkit-bg-img::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: linear-gradient(0deg, rgb(54 58 199 / 38%), rgba(0, 0, 0, 0.2));
}
.wkit-img-box.style-2:hover > .wkit-img-glitch{
    opacity: 1;
    transition: transform 500ms cubic-bezier(0.2, 1, 0.3, 1);
    transform: translateZ(-200px) rotateX(60deg) rotateY(0) rotateZ(-35deg) !important;
}
.wkit-img-box:hover > .wkit-img-glitch{
    opacity: 1;
    transition: transform 500ms cubic-bezier(0.2, 1, 0.3, 1);
    /*transform: translateZ(-200px) rotateX(60deg) rotateY(0) rotateZ(-35deg);*/
}

.wkit-img-box > .wkit-img-glitch{
    position: absolute;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    opacity: 0;
    border-radius:0;
}
.wkit-img-glitch > .wkit-glitch{
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: var(--img);
    background-size: cover;
    background-position: 50% 50%;
    transform: translate3d(0, 0, 0);
    image-rendering: pixelated;
    background-color: #f3727e;
    background-blend-mode: none;
}
.wkit-img-glitch .wkit-glitch:nth-child(2) {
    background-color: #f3727e;
    background-blend-mode: color-dodge;
}

.wkit-img-glitch .wkit-glitch:nth-child(3) {
    background-color: #3124c7;
    background-blend-mode: luminosity;
}

.wkit-img-glitch .wkit-glitch:nth-child(4) {
    background-color: #3124c7;
    background-blend-mode: none;
}

.wkit-img-glitch .wkit-glitch:nth-child(5) {
    background-color: #f3727e;
    background-blend-mode: none;
}
.wkit-img-glitch .wkit-glitch:nth-child(n+2) {
    opacity: 0;
}

/* Hovers */
/* On hover we show the 2nd, 3rd, 4th and 5th image*/
.wkit-img-box:hover .wkit-glitch:nth-child(n+2) {
    opacity: 1;
}

/* Hover animations for horizontal case */
.wkit-img-box:hover .wkit-glitch:nth-child(2) {
    transform: translate3d(20px, 0, 0);
    animation: glitch-anim-1-horizontal 1s infinite linear alternate;
}

.wkit-img-box:hover .wkit-glitch:nth-child(3) {
    transform: translate3d(calc(-1 * (20px)), 0, 0);
    animation: glitch-anim-2-horizontal 1s infinite linear alternate;
}

.wkit-img-box:hover .wkit-glitch:nth-child(4) {
    transform: translate3d(0, calc(-1 * (5px)), 0) scale3d(-1, -1, 1);
    animation: glitch-anim-3-horizontal 1s infinite linear alternate;
}

/* Hover flash animation on last image */
.wkit-img-box:hover .wkit-glitch:nth-child(5) {
    animation: glitch-anim-flash 0.5s steps(1, end) infinite;
}

.wkit-img-box:hover .wkit-img-content{
    opacity: 1;
    color: #f3727e;
}

.wkit-img-box .wkit-img-content{
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    color: #fff;
    padding: 20px;
}
.wkit-img-content > .wkit-img-title{
    font-family: park-lane, serif;
    font-size: 30px;
    line-height: 40px;
    color:#000;
}
.wkit-img-content > .wkit-img-label{
    font-size: 12px;
